package model;

public class Ve325 {
	private int id;
	private int lichchieu;
	private String ghe;
	private int giave;
	private int hoadon;
	private int khachhang;
	private int phim;

	public Ve325() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Ve325(int id, int lichchieu, String ghe, int giave, int hoadon, int khachhang, int phim) {
		super();
		this.id = id;
		this.lichchieu = lichchieu;
		this.ghe = ghe;
		this.giave = giave;
		this.hoadon = hoadon;
		this.khachhang = khachhang;
		this.phim = phim;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public int getLichchieu() {
		return lichchieu;
	}

	public void setLichchieu(int lichchieu) {
		this.lichchieu = lichchieu;
	}

	public String getGhe() {
		return ghe;
	}

	public void setGhe(String ghe) {
		this.ghe = ghe;
	}

	public int getGiave() {
		return giave;
	}

	public void setGiave(int giave) {
		this.giave = giave;
	}

	public int getHoadon() {
		return hoadon;
	}

	public void setHoadon(int hoadon) {
		this.hoadon = hoadon;
	}

	public int getKhachhang() {
		return khachhang;
	}

	public void setKhachhang(int khachhang) {
		this.khachhang = khachhang;
	}

	public int getPhim() {
		return phim;
	}

	public void setPhim(int phim) {
		this.phim = phim;
	}

	// Constructors, getters, setters
}
